import React from "react";
import './Home.css';
import heroImg from "./../hero-img.png";

export default function Home() {
  return (
  <>
  
    <div className="home-card">
        <img src={heroImg} alt="Customer" className="heroimg" />
       <div className="card-text">
        <div className="herotext">
       
            <h4>Customer Relationship Management</h4>
            <h1>Build<br/>Your Loyal<br/>Customer</h1>
            <p>support a global network that includes large-<br/> scale businesses in more than 100 countries to<br/>generate more leads.</p>
            <button type="button" className="btn btn-primary herobtn">Get started</button>
        </div>
        </div>
    </div>
  
  <div className="home-card">
      <div className="card-text"></div>
      <img src="" alt="" />

  </div>
  
  
  </>
  );
}
